    (23, 'c8e92de5-4302-48ad-b575-a014f187251e', 1, 'entitytypedefinition-businessentity-emailaddress', 'EmailAddress', 'Uniquely identifies a conceptual electronic mail (email) box to which email messages may be delivered via the Internet.  An EmailAddress conforms to the addr-spec in Section 3.4 of the Internet Engineering Task Force (IETF) Request for Comments (RFC) 5322 may optionally include a DisplayName but must include a local part and an Internet domain, separated by an at symbol (@).', 'Eml Adr', '000.000.000', 4, 1, '2025-07-10 15:38:13.277277', -100, '', 131, true, '83b0361c-108f-4fe6-b95b-fc6f6d645e76', 'ba6b74a0fc6643b5a6095ecb4dc05ce0e175ed811cb64fe2073f1156e78edf30', '2025-07-10 13:02:04.801488', -100, '2025-07-10 15:38:22.135311', -100, '9999-12-31 23:59:59.999', -1),
    (24, 'bf81c65c-f5ff-4a22-90e5-2f04945177d6', 1, 'entitytypedefinition-businessentity-uniformresourceidentifier', 'UniformResourceIdentifier', 'As defined in the Internet Engineering Task Force (IETF) Request for Comment (RFC) 3986, Uniform Resource Identifier (URI) made up of a string of characters that uniquely identifies an abstract or physical resource such as a document that can be viewed or downloaded or a web application programming interface (API).  A URL (Uniform Resource Locator) is a specific type of URI that also indicates how to access the resource.  In this case, this is specified using the EntitySubtype value.  An EntitySubtype value of 0 (None) indicates a URI, rather than a URL.', 'URI', '000.000.000', 4, 1, '2025-07-12 12:21:41.309129', -100, '', 132, true, '83b0361c-108f-4fe6-b95b-fc6f6d645e76', '6313f51f393f9e68318783d1b7436e2fa2c757d9d3c73d898a6ca3b397e0f0cc', '2025-07-12 11:12:58.709102', -100, '2025-07-12 12:21:54.060773', -100, '9999-12-31 23:59:59.999', -1),
    (25, '4c70b3a5-624c-460a-bc3b-ea53239b0302', 1, 'entitytypedefinition-businessentity-informationsystem', 'InformationSystem', 'An integrated collection of digital assets for collecting, storing, and processing data.  An InformationSystem typically belongs to/is controlled by a LegalEntity, is often accessible using a UniformResourceLocator (URL), and may have zero or more InformationSystemUsers.', 'Inf Sys', '000.000.000', 4, 1, '2025-07-15 12:41:52.46639', -100, '', 140, true, '83b0361c-108f-4fe6-b95b-fc6f6d645e76', 'cedc35ee01c4b9f8f200288828779897057e5f91367e6be5430aa480d1c0b822', '2025-07-15 12:05:04.683491', -100, '2025-07-15 12:41:58.656822', -100, '9999-12-31 23:59:59.999', -1),
    (26, '435ffe3c-a973-4a13-a7fd-f4de92f6278e', 1, 'entitytypedefinition-businessentity-contentitem', 'ContentItem', 'Content is information, i.e. data with contextual meaning, enriched using relationships, organizational structure, etc, that is expressed or delivered to an audience via a specific medium, e.g. speech, writing, still or moving images, etc, for the purpose of expression, communication, or commerce.\n\nContent may be created and edited for the public by authors, subject matter experts, and other content creators. However, not all content requires creative or authoritative authoring or editing.\n\nNote: The word "content" implies a container, the nature of which is often determined by the medium.\n\nTherefore, a ContentItem is an individual instance of content.', 'Cnt Itm', '000.000.000', 4, 1, '2025-08-18 11:57:28.093621', -100, '', 220, true, '83b0361c-108f-4fe6-b95b-fc6f6d645e76', 'ee11520f6f16cbcb6bfb791be3e213d7a2ffc9213c56a7e93234b113d0b10fd5', '2025-08-18 10:47:14.289592', -100, '2025-08-18 11:57:33.333', -100, '9999-12-31 23:59:59.999', -1),
