    (9, '4593af3a-032c-46ed-9462-50654d7a43b3', 1, 'entitytype-entity-language', 'Language', 'A system of spoken words or sounds, written symbols, and rules used for communication', 'Lang', '', -1, true, '00000000-0000-0000-0000-000000000000', '15e9daaee6b84b30d894cfda43302a58d52d3a4ba806f10ed3818175063e7ef8', '2022-06-01 00:00:00.000', 0, '2022-06-01 00:00:00.000', 0, '9999-12-31 23:59:59.999', 0),
    (10, 'bea276a4-c36b-4445-86a9-6b948147e65a', 1, 'entitytype-entity-locale', 'Locale', 'The combination of a Language and a GeographicUnit that represents regional linguistic variations, e.g. U.S. vs U.K. English, and that is necessary in order to meaningfully communicate, including the valid representation of dates, times, currency amounts, etc', 'Locale', '', -1, true, '00000000-0000-0000-0000-000000000000', '656932e024a250420f5ab706f892641a4e9b9e78120d1355f8d8783bc0661ca8', '2022-06-01 00:00:00.000', 0, '2022-06-01 00:00:00.000', 0, '9999-12-31 23:59:59.999', 0),
    (11, 'f1726565-e70b-4e92-a6fd-1f1f0a1cd922', 1, 'entitytype-entity-currency', 'Currency', 'Represents one of a country''s offical, standardized, physical or virtual mediums of exchange for goods or services', 'Curr', '', -1, true, '00000000-0000-0000-0000-000000000000', '603b9875feca4c54e6a9b6955d2f86e55a6ef8084ca6a0a592e75e4710e58443', '2022-06-01 00:00:00.000', 0, '2022-06-01 00:00:00.000', 0, '9999-12-31 23:59:59.999', 0),
    (12, '6a508aa6-49b6-4150-a0fc-5a737aee9fe0', 1, 'entitytype-entity-periodicity', 'Periodicity', 'An entity''s tendency to recur at regular intervals, e.g. hourly, daily, weekly, monthly, yearly, etc', 'Per', '', -1, true, '00000000-0000-0000-0000-000000000000', 'df336129cf0580677b6e71b1ffc0a81047a9c2ce03e838f5c58d7d38c822b1e2', '2022-06-01 00:00:00.000', 0, '2022-06-01 00:00:00.000', 0, '9999-12-31 23:59:59.999', 0),
    (13, '4005abaf-ef1e-423b-abb1-2f5913bd2ab2', 1, 'entitytype-entity-postaladdress', 'PostalAddress', 'A standardized, structured specification for a physical location to which mail can be sent by a delivery service and made up of elements such as street number, street name, city name, geographic or administrative subdivision (e.g. state, province, etc), postal code, country name, etc', 'Post Adr', '', -1, true, '00000000-0000-0000-0000-000000000000', '087ac8378bb27aa8a948255de297b170132c24432072ae006560f8862d75e07b', '2022-06-01 00:00:00.000', 0, '2022-06-01 00:00:00.000', 0, '9999-12-31 23:59:59.999', 0),
    (14, 'a0f1152b-da0f-43da-b3d2-979a24a96eb7', 1, 'entitytype-entity-person', 'Person', 'An individual that has legal standing according to the law and that has the legal ability to enter into formal and informal contracts, assume business obligations, incur and pay debts, sue and be sued in its own right and be held responsible for its individual actions. Note: Corporations, government agencies, etc are all Organizations at their "core" while vendors, customers, etc can be either Organizations or individual Persons.  Persons employed by Organizations can be represented as Employees, and Persons or Employees who are authorized to access and interact with InformationSystems are represented as InformationSystemUsers', 'Person', '', -1, true, '00000000-0000-0000-0000-000000000000', '2473e8cd73697c075e064c74817d5c9dd9b01b9c70e8c8da57e01defd3f49a2f', '2022-06-01 00:00:00.000', 0, '2022-06-01 00:00:00.000', 0, '9999-12-31 23:59:59.999', 0),
    (15, '30abc73d-7c18-4480-a5e8-bc68d907960d', 1, 'entitytype-entity-organization', 'Organization', 'A corporate legal entity e.g. proprietorship, partnership, or corporation made up of a group of individual Persons and/or other Organizations that has legal standing according to the law and that has the legal ability to enter into formal and informal contracts, assume business obligations, incur and pay debts, sue and be sued in its own right, and be held responsible for its corporate actions.  Note: Corporations, government agencies, etc are all Organizations at their "core" while vendors, customers, etc can be either Organizations or individual Persons.  Organizations are often internally structured using OrganizationalUnits e.g. department, division, region, etc', 'Org', '', -1, true, '00000000-0000-0000-0000-000000000000', '976d1ba03bb15485f34a461d83828b0d4db6f9e8280aabe52acd5e1ca95b5ab0', '2022-06-01 00:00:00.000', 0, '2022-06-01 00:00:00.000', 0, '9999-12-31 23:59:59.999', 0),
    (16, '7b365cf8-efbf-40ba-a47d-665c6361fbe1', 1, 'entitytype-entity-organizationalunit', 'OrganizationalUnit', 'An instance of an Organization''s internal organizational structure e.g. a department, a division, a region, etc.  OrganizationalUnits can reflect a functional structure, e.g. Marketing Department, Finance Department, Sales Department, etc, a geographic structure, e.g. Eastern Division, Western Division, etc, or any other organizational structure', 'Org Unit', '', -1, true, '00000000-0000-0000-0000-000000000000', '5b9eaee9fdada84b4930898d2cfb88b50cebbe8a103dfb39e58c14edc1a895d5', '2022-06-01 00:00:00.000', 0, '2022-06-01 00:00:00.000', 0, '9999-12-31 23:59:59.999', 0),
    (17, 'd60c421e-b8c3-4b59-918c-15f0be7a1277', 3, 'entitytype-hierarchy-organizationalunithierarchy', 'OrganizationalUnitHierarchy', 'An OrganizationalUnit''s location in an Organization''s internal organizational structure e.g. one of several teams within a department, a region within a division, etc.  OrganizationalUnitHierarchys enable us to re-organize OrganizationalUnits by changing their relative locations in the hierarchy without changing the internal state of the OrganizationalUnits themselves', 'Org Unit Hier', '', -1, true, '00000000-0000-0000-0000-000000000000', 'aa9e720d0f43b5409e1d5e4e8f24dbf27b1a2222b8daa64d98ef67a06a2650c2', '2022-06-01 00:00:00.000', 0, '2022-06-01 00:00:00.000', 0, '9999-12-31 23:59:59.999', 0),
    (18, 'a79bc3c2-ef1e-4d91-9275-f784f9a757ed', 1, 'entitytype-entity-employee', 'Employee', 'A Person who is employed to work for an Organization.  A Person may be employed by one or more Organizations, which will result in the Person having one or more Employee Ids.  An Employee may be assigned to zero or more of his/her Organization''s OrganizationalUnits and is assigned to one or more EmployeeRoles', 'Empl', '', -1, true, '00000000-0000-0000-0000-000000000000', 'a9e448edcb092a87672fec35da7e4958c74f1617f73876324e49fb1928901e6e', '2022-06-01 00:00:00.000', 0, '2022-06-01 00:00:00.000', 0, '9999-12-31 23:59:59.999', 0),
    (19, '76ffe754-244b-447f-a192-d5ed11222c70', 1, 'entitytype-entity-legalentity', 'LegalEntity', 'Either an individual Person or a corporate legal entity i.e. an Organization that has legal standing according to the law and has the legal ability to enter into formal and informal contracts, assume business obligations, incur and pay debts, sue and be sued in its own right, and be held responsible for its actions.  Note: Abstracting Person, Employee, or Organization as a LegalEntity simplifies the code that would otherwise be needed to manage these different entity types', 'Legal Ent', '', -1, true, '00000000-0000-0000-0000-000000000000', 'caf38ce7fb03328aa20b192cacfad9e63435ee67cdfab195431d7c07d33271e2', '2022-06-01 00:00:00.000', 0, '2022-06-01 00:00:00.000', 0, '9999-12-31 23:59:59.999', 0),
    (20, 'd290611a-b6be-4200-a681-bed5720b115f', 1, 'entitytypedefinition-businessentity-internetdomainlabel', 'InternetDomainLabel', 'Represents an area of administrative authority or control within the Internet.  An Internet domain typically belongs to and is controlled by a LegalEntity.  InternetDomainLabels are the building blocks of hierarchically structured Internet domain names, e.g. from left to right (optional) third-level domain (or subdomain) dot second-level domain (SLD) dot top-level domain (TLD) like photos.google.com.', 'Int Domn Lbl', '', 120, true, '83b0361c-108f-4fe6-b95b-fc6f6d645e76', '239f46b7caa2a974e99e62539827b941d44b0f51ed21b9b6c9e3f2f0276ec268', '2025-07-01 15:12:24.61689', -100, '2025-07-01 15:12:24.61689', -100, '9999-12-31 23:59:59.999', 0),
    (21, 'f458139b-c668-4544-872d-f8bc281ec3a7', 3, 'entitytypedefinition-entityhierarchy-internetdomainlabelhierarchy', 'InternetDomainLabelHierarchy', 'Defines hierarchical parent/child relationships between InternetDomainLabels, resulting in tree structures that represent containment or membership, e.g. these second-level domains (SLDs) belong to this top-level domain (TLD), etc', 'Int Dom Lbl Hir', '', 121, true, '83b0361c-108f-4fe6-b95b-fc6f6d645e76', '6917eb528d681dee7cddcfedb41e3fa2e06294e8776609381f71d26ef7113ae1', '2025-07-03 14:57:56.154628', -100, '2025-07-03 14:57:56.154628', -100, '9999-12-31 23:59:59.999', -1),
    (22, 'aef07383-cd47-4324-933d-e5f13026a07d', 1, 'entitytypedefinition-businessentity-telephonenumber', 'TelephoneNumber', 'Represents an International Telecommunication Union (ITU) telephone number, compliant with the E.164 standard', 'Phone Nmbr', '', 130, true, '83b0361c-108f-4fe6-b95b-fc6f6d645e76', '7aa1ae98e498e7857bcc71fb25a694d4b354d45ad67dbe85c9328f3e1d3ba6de', '2025-07-04 11:11:15.957006', -100, '2025-07-04 11:11:15.957006', -100, '9999-12-31 23:59:59.999', -1),
    (23, '74e8906b-b545-482b-9959-6c1aceaaed28', 1, 'entitytypedefinition-businessentity-emailaddress', 'EmailAddress', 'Uniquely identifies a conceptual electronic mail (email) box to which email messages may be delivered via the Internet.  An EmailAddress conforms to the addr-spec in Section 3.4 of the Internet Engineering Task Force (IETF) Request for Comments (RFC) 5322 may optionally include a DisplayName but must include a local part and an Internet domain, separated by an at symbol (@).', 'Eml Adr', '', 131, true, '83b0361c-108f-4fe6-b95b-fc6f6d645e76', 'f063b2bd6859f871a0712e8b6a5d62f5a58bc6efb3aa007ffa10ce20b4989248', '2025-07-10 15:38:30.976005', -100, '2025-07-10 15:38:30.976005', -100, '9999-12-31 23:59:59.999', -1),
    (24, '0676c57a-b52e-4884-bf33-9878c3f4a827', 1, 'entitytypedefinition-businessentity-uniformresourceidentifier', 'UniformResourceIdentifier', 'As defined in the Internet Engineering Task Force (IETF) Request for Comment (RFC) 3986, Uniform Resource Identifier (URI) made up of a string of characters that uniquely identifies an abstract or physical resource such as a document that can be viewed or downloaded or a web application programming interface (API).  A URL (Uniform Resource Locator) is a specific type of URI that also indicates how to access the resource.  In this case, this is specified using the EntitySubtype value.  An EntitySubtype value of 0 (None) indicates a URI, rather than a URL.', 'URI', '', 132, true, '83b0361c-108f-4fe6-b95b-fc6f6d645e76', '970e545c6f1de0b07a25d58fb3ea7a5105f1f410d1bf648e678332cf8296aab1', '2025-07-12 12:21:54.150319', -100, '2025-07-12 12:21:54.150319', -100, '9999-12-31 23:59:59.999', -1),
    (25, '4dc95ded-ff9e-4a70-9e33-abbdcaab736f', 1, 'entitytypedefinition-businessentity-informationsystem', 'InformationSystem', 'An integrated collection of digital assets for collecting, storing, and processing data.  An InformationSystem typically belongs to/is controlled by a LegalEntity, is often accessible using a UniformResourceLocator (URL), and may have zero or more InformationSystemUsers.', 'Inf Sys', '', 140, true, '83b0361c-108f-4fe6-b95b-fc6f6d645e76', '72d3ca7dd59141932be15802acb4ac4a322f2f57c61204e66a9938fc0d32b0e9', '2025-07-15 12:41:58.715711', -100, '2025-07-15 12:41:58.715711', -100, '9999-12-31 23:59:59.999', -1),
    (26, '8c1e1288-8dd0-4561-afe6-27393608b62f', 1, 'entitytypedefinition-businessentity-contentitem', 'ContentItem', 'Content is information, i.e. data with contextual meaning, enriched using relationships, organizational structure, etc, that is expressed or delivered to an audience via a specific medium, e.g. speech, writing, still or moving images, etc, for the purpose of expression, communication, or commerce.\n\nContent may be created and edited for the public by authors, subject matter experts, and other content creators. However, not all content requires creative or authoritative authoring or editing.\n\nNote: The word "content" implies a container, the nature of which is often determined by the medium.\n\nTherefore, a ContentItem is an individual instance of content.', 'Cnt Itm', '', 140, true, '83b0361c-108f-4fe6-b95b-fc6f6d645e76', '7c77228193ce7c1e1f76c9acd7e070c806dfacceabb73c739be1bd053ee1538e', '2025-07-15 12:41:58.715711', -100, '2025-07-15 12:41:58.715711', -100, '9999-12-31 23:59:59.999', -1),
    (27, 'a6d693ea-adf5-4fc3-8f99-c7465ba8052b', 1, 'entitytypedefinition-businessentity-contentitemgroup', 'ContentItemGroup', 'A grouping mechanism for ContentItems that may be related by subject, audience, etc -- a ContentItemGroup enables one or more ContentItems to be associated with one or more ContentItemGroups for display, browsing, etc.', 'Cnt Itm Grp', '', 221, true, '83b0361c-108f-4fe6-b95b-fc6f6d645e76', '98e162c0331ca5eb550f6411ef3e4eba58aab9f3a03555d905a3681a2b862386', '2025-08-19 11:48:51.967655', -100, '2025-08-19 11:48:51.967655', -100, '9999-12-31 23:59:59.999', -1),
    (28, 'ccccc137-8eeb-4d0f-b528-b356f3c10be8', 2, 'entitytypedefinition-entityassociation-contentitemgroupcontentitemassociation', 'ContentItemGroupContentItemAssociation', 'Associates zero or more ContentItems with each ContentItemGroup', 'Grp CntItm Asc', '', 222, true, '83b0361c-108f-4fe6-b95b-fc6f6d645e76', '530b0cac82eef996a556ad665f7c1c20d04d61bb38e15c3aa9c2d893eb6d5bed', '2025-08-19 13:23:06.918781', -100, '2025-08-19 13:23:06.918781', -100, '9999-12-31 23:59:59.999', -1),
    (29, 'fe99add0-21f2-4ec1-a946-d2a2abe0ee5d', 1, 'entitytypedefinition-businessentity-keyword', 'Keyword', 'A significant attribute value for a given entity instance, e.g. the value \"Red\" for the attribute \"Color\", or descriptor (adjective, e.g. \"red hat\", title, e.g. \"The Red Badge of Courage\", description, summary, or content, e.g. \"that turns red when\").\n\nKeywords can be used as a tags/hashtags, search engine input, etc in order to establish/clarify identity, discover location (i.e. used as an index), clarify semantic meaning, organize and/or classify, or aggregate (cluster) metadata, e.g. \"''red'' occurs 5 times in this description\".\n\nKeywords are often used to request information and for navigation (in a hierarchy or search).', 'Kywd', '', 320, true, '5595adaa-0312-4983-82e7-1127e3c43444', '634155bb9599bebb60b1e925eb7a03514fa43cd4a8889b01cb487ce1c4366d37', '2025-08-20 13:19:12.350715', -100, '2025-08-20 13:19:12.350715', -100, '9999-12-31 23:59:59.999', -1),
    (30, 'a352f4aa-4601-448c-8aab-b26e907a6edb', 1, 'entitytypedefinition-businessentity-keywordgroup', 'KeywordGroup', 'A grouping mechanism for Keywords that may be related by purpose, subject, etc -- a KeywordGroup enables one or more Keywords to be associated with one or more KeywordGroups for navigation, search, etc.', 'Kywd Grp', '', 321, true, '83b0361c-108f-4fe6-b95b-fc6f6d645e76', '305995065ccf1b048200ccd3429a879e272be01999171a34899813405527b2d0', '2025-08-20 13:57:03.130853', -100, '2025-08-20 13:57:03.130853', -100, '9999-12-31 23:59:59.999', -1),
    (31, '99009cc9-cffc-445f-9ee4-8e047d45e3ad', 2, 'entitytypedefinition-entityassociation-keywordgroupkeywordassociation', 'KeywordGroupKeywordAssociation', 'Associates zero or more Keywords with each KeywordGroup', 'Grp Kywd Asc', '', 322, true, '83b0361c-108f-4fe6-b95b-fc6f6d645e76', 'eab4f52974d1ea3f6608f90f8bb8cf5914dd3317839b46c12d348ad2542c362b', '2025-08-20 15:09:59.402102', -100, '2025-08-20 15:09:59.402102', -100, '9999-12-31 23:59:59.999', -1)
